import React from "react";
import { Line } from "react-chartjs-2";
import { Col, Row, Typography } from "antd";
import CryptoDetails from "./CryptoDetails";

const LineChart = ({ coinHistory, currentPrice, coinName }) => {
  const coinPrice = [];
  const timeStamp = [];

  for (let i = 0; i < coinHistory?.history?.length; i++) {
    coinPrice.push(coinHistory.history[i].price);
    console.log(coinHistory.history[i].price);
    timeStamp.push(
      new Date(coinHistory.history[i].timeStamp).toLocaleDateString
    );
  }

  const data = {
    labels: timeStamp,
    dataset: [
      {
        label: "USD",
        data: coinPrice,
        fill: false,
        backgroundColor: "#FFFFFF",
        borderColor: "#000",
      },
    ],
  };

  const options = {
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  };

  return (
    <>
      <Col style={{ marginTop: "5%" }}>
        <Typography.Title level={3}>{coinName} Price Chart</Typography.Title>

        <Typography.Title level={4}>
          Daily Change: {coinHistory?.change}{" "}
        </Typography.Title>
        <Typography.Title level={4}>
          Current Price: {currentPrice}
        </Typography.Title>
      </Col>
      <Line data={data}  />
    </>
  );
};

export default LineChart;
