import React, {useState, useEffect} from 'react'
import { Button, Menu, Typography, Avatar } from 'antd'
import {Link} from 'react-router-dom'
import {HomeOutlined, MoneyCollectionOutlined, BulbOutlined, FundOutlined, MenuOutlined} from '@ant-design/icons'
import icon from '../images/logo.png'
import 'antd/dist/antd.css'
import './styles.css'

const Navbar = () => {

    const [activeWindow, setActiveWindow] = useState(true);
    const [size, setSize] = useState(null);

    useEffect(() => {
        const handleResize = () => setSize(window.innerWidth);
        window.addEventListener('resize', handleResize);
        handleResize();
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    useEffect(() => {

    }, [size])
    return (
        <div className="nav-container" style={{height: "100%"}}>
            <Menu theme="dark" style={{display: 'flex', height: '100%', flexDirection: 'column', padding:"1rem"}}>
            <div className="logo-container">
                <Avatar src={icon} size="large"/>
                <Typography.Title level={2} className="logo">
                    <Link to="/">Creepto</Link>
                </Typography.Title>
            </div>
                <Menu.Item icon={<HomeOutlined />}>
                    <Link to="/">Home</Link>
                </Menu.Item>
                <Menu.Item icon={<FundOutlined />}>
                    <Link to="/cryptocurrencies">Cryptocurrencies</Link>
                </Menu.Item>
                <Menu.Item icon={<BulbOutlined />}>
                    <Link to="/exchanges">Exchanges</Link>
                </Menu.Item>
                <Menu.Item icon={<MenuOutlined />}>
                    <Link to="/news">News</Link>
                </Menu.Item>
            </Menu>
        </div>
    )
}

export default Navbar
