import React from "react";
import { Select, Typography, Row, Col, Avatar, Card } from "antd";
import moment from "moment";
import { useGetCryptoNewsQuery } from "../services/cryptoNewsApi";

const { Text, Title } = Typography;
const { Option } = Select;

const News = ({ simplified }) => {
  const { data: cryptoNews, isFetching } = useGetCryptoNewsQuery({
    newsCategory: "Cryptocurrency",
    count: simplified ? 6 : 20,
  });
  if (isFetching) return "Loading..";
  console.log(cryptoNews);
  return (
    <>
      <Row gutter={[24, 24]} style={{ margin: "1%" }}>
        {cryptoNews.value.map((news, i) => (
          <Col xs={24} s={12} lg={8} key={i}>
            <Card hoverable>
              <a href={news.url} target="_blank" rel="noreferrer">
                <div
                  style={{ display: "grid", gridTemplateColumns: "2fr 2fr" }}
                >
                  <div>
                    <Title level={4}>{news.name}</Title>
                  </div>
                  <div style={{ textAlign: "right" }}>
                    <img
                      src={news?.image?.thumbnail?.contentUrl}
                      alt="news thumbnail"
                    />
                  </div>
                </div>

                <p
                  style={{ textAlign: "left", color: "grey", marginTop: "6%" }}
                >
                  {news?.description}
                </p>
                <div style={{ textAlign: "right"}}>
                  <Avatar
                    src={news.provider[0]?.image?.thumbnail?.contentUrl}
                    alt="news"
                    style={{ height: "4vh" }}
                  />
                 <Text style={{paddingLeft: "3%"}}>{news.provider[0]?.name}</Text>
                 <Text style={{textAlign: "right"}}>{moment(news.datePublished).startOf('ss').fromNow()}</Text>
                </div>

              </a>
            </Card>
          </Col>
        ))}
      </Row>
    </>
  );
};

export default News;
