import React from "react";
import { Line } from "react-chartjs-2";
import { Col, Row, Typography } from "antd";
import CryptoDetails from "./CryptoDetails";
const lineChart = ({ coinHistory, currentPrice, coinName }) => {

    const coinPrice = [];
    const timeStamp = [];

    for (let i = 0; i < coinHistory?.history?.length; i++) {
        coinPrice.push(coinHistory.history[i].price);
        timeStamp.push(new Date(coinHistory.history[i].timeStamp).toLocaleDateString);
    }
    return (
    <>
      <Col style={{ marginTop: "5%" }}>
        <Typography.Title level={3}>{coinName} Price Chart</Typography.Title>

        <Typography.Title level={4}>
          Daily Change: {coinHistory?.change}{" "}
        </Typography.Title>
        <Typography.Title level={4}>
          Current Price: {currentPrice}
        </Typography.Title>
      </Col>
      <Line></Line>
    </>
  );
};

export default lineChart;
