import React, { useState } from "react";
import HTMLReactParser from "html-react-parser";
import { useParams } from "react-router-dom";
import millify from "millify";
import { Col, Row, Typography, Select, Avatar } from "antd";
import {
  MoneyCollectOutlined,
  DollarCircleOutlined,
  FundOutlined,
  ExclamationCircleOutlined,
  StopOutlined,
  TrophyOutlined,
  CheckOutlined,
  NumberOutlined,
  ThunderboltOutlined,
} from "@ant-design/icons";
import { useGetCryptoDetailsQuery } from "../services/cryptoApi";

const { Title, Text } = Typography;
const { Option } = Select;

const CryptoDetails = () => {
  const { coinId } = useParams();
  const [timePeriod, setTimePeriod] = useState("7d");
  const { data: coinDetail, isFetching } = useGetCryptoDetailsQuery(coinId);
  const cryptoDetails = coinDetail?.data?.coin;
  const time = ["3h", "24h", "7d", "30d", "1y", "3m", "3y", "5y"];

  const cryptoPrice = cryptoDetails.price;

  const stats = [
    {
      title: "Price to USD",
      value: cryptoPrice,
      icon: <DollarCircleOutlined />,
    },
    { title: "Rank", value: cryptoDetails.rank, icon: <NumberOutlined /> },
    {
      title: "24h Volume",
      value: `$ ${cryptoDetails.volume && millify(cryptoDetails.volume)}`,
      icon: <ThunderboltOutlined />,
    },
    {
      title: "Market Cap",
      value: `$ ${cryptoDetails.marketCap && millify(cryptoDetails.marketCap)}`,
      icon: <DollarCircleOutlined />,
    },
    {
      title: "All-time-high(daily avg.)",
      value: `$ ${millify(cryptoDetails.allTimeHigh.price)}`,
      icon: <TrophyOutlined />,
    }
  ];

  const genericStats = [
    {
      title: "Number Of Markets",
      value: cryptoDetails.numberOfMarkets,
      icon: <FundOutlined />,
    },
    {
      title: "Number Of Exchanges",
      value: cryptoDetails.numberOfExchanges,
      icon: <MoneyCollectOutlined />,
    },
    {
      title: "Approved Supply",
      value: cryptoDetails.approvedSupply ? (
        <CheckOutlined />
      ) : (
        <StopOutlined />
      ),
      icon: <ExclamationCircleOutlined />,
    },
    {
      title: "Total Supply",
      value: `$ ${millify(cryptoDetails.totalSupply)}`,
      icon: <ExclamationCircleOutlined />,
    },
    {
      title: "Circulating Supply",
      value: `$ ${millify(cryptoDetails.circulatingSupply)}`,
      icon: <ExclamationCircleOutlined />,
    }
  ];

  if (isFetching) return "Loading...";
  const sex = cryptoDetails.price;

  console.log({sex});
  return (
    <Col>
      <Col>
        <div>
          <div style={{ textAlign: "center" }}>
            <Avatar
              src={cryptoDetails.iconUrl}
              style={{ margin: "2%", height: "7vh", width: "7vh" }}
            />
          </div>
          <div style={{ textAlign: "center" }}>
            <Title level={2} style={{ fontWeight: "200" }}>
              {cryptoDetails.name} ({cryptoDetails.slug})
            </Title>
          </div>

          <div style={{ textAlign: "center" }}>
            <p style={{}}>{cryptoDetails.name} Live Statistics</p>
            <Select
              defaultValue="7d"
              placeholder="Select Time Period"
              onChange={(value) => setTimePeriod(value)}
            >
              {time.map((date) => (
                <Option key={date}>{date}</Option>
              ))}
            </Select>
          </div>




          {/* {stats.map(({ icon, title, value }) => {
            <Col>
              <Text>{icon}</Text>
              <Text>{title}</Text>
              <Text>{value}</Text>
            </Col>;
          })} */}
        </div>
      </Col>
    </Col>
  );
};

export default CryptoDetails;
